import React, { Component } from 'react'
import { connect } from 'react-redux'
// import PropTypes from 'prop-types'
// import FontAwesomeIcon from '@fortawesome/react-fontawesome'
// import faCog from '@fortawesome/fontawesome-free-solid/faCog'


import './index.css';
import {
  onGetTradeHistory,
} from 'actions/tradeHistory'
//Components
// import PagesList from 'components/Pages'


class TradeHistory extends Component {
  componentDidMount() {
    this.props.onGetTradeHistory()
  }

  render() {
    console.log(this.props.dataTradeHistory)
    return (
      <div>
        TradeHistory container
        <br />
        {this.props.dataTradeHistory.length ?
          <div>
            <ul>
              {
                this.props.dataTradeHistory.map((item, num) => {
                  return (
                    <li key={num}>{item.date}</li>
                  )
                })
              }
            </ul>
            Всего записей: {this.props.dataTradeHistory.length}
          </div>
          :
          null
        }
      </div>
    )
  }
}


// TradeHistory.propTypes = {
//   data: PropTypes.string,
// }


const mapStateToProps = (state) => ({
  dataTradeHistory: state.tradeHistory.data
})

const mapDispatchToProps = ({
  onGetTradeHistory,
})

export default connect(mapStateToProps, mapDispatchToProps)(TradeHistory);
